  /**
   * 
   */
  updateView() {
    if (!this.overlay) return;
    this.overlay.innerHTML = "";
    const cornerButton = this.createCornerButton();
    if (cornerButton) {
      this.overlay.appendChild(cornerButton);
    }
    if (this.isLandscape) {
      this.gameStartContainer = this.createGameStart();
      this.overlay.appendChild(this.gameStartContainer);
    } else {
      this.rotateHintContainer = this.createRotateHint();
      this.overlay.appendChild(this.rotateHintContainer);
    }
  }
  /**
   * 
   */
  async requestFullscreen() {
    console.log("requestFullscreen12123");
    try {
      const element = document.documentElement;
      if (element.requestFullscreen) {
        await element.requestFullscreen();
      } else if (element.webkitRequestFullscreen) {
        await element.webkitRequestFullscreen();
      } else if (element.mozRequestFullScreen) {
        await element.mozRequestFullScreen();
      } else if (element.msRequestFullscreen) {
        await element.msRequestFullscreen();
      }
      await this.lockOrientation();
      this.removeOverlay();
      this.setupFullscreenExitListener();
      if (this.options.enterCallback && typeof this.options.enterCallback === "function") {
        this.options.enterCallback();
      }
      if (this.options.showPromo) {
        setTimeout(() => {
          this.showPromoBanner();
        }, 500);
      }
    } catch (error) {
      this.removeOverlay();
      this.setupFullscreenExitListener();
      if (this.options.enterCallback && typeof this.options.enterCallback === "function") {
        this.options.enterCallback();
      }
      console.error("Failed to enter fullscreen:", error);
    }
  }
  /**
   * 
   */
  async lockOrientation() {
    try {
      if (screen.orientation && screen.orientation.lock) {
        await screen.orientation.lock("landscape");
        console.log("Screen orientation locked to landscape");
      } else if (screen.lockOrientation) {
        screen.lockOrientation("landscape");
      } else if (screen.mozLockOrientation) {
        screen.mozLockOrientation("landscape");
      } else if (screen.msLockOrientation) {
        screen.msLockOrientation("landscape");
      }
    } catch (error) {
      console.warn("Failed to lock screen orientation:", error);
    }
  }
  /**
   * 
   */
  removeOverlay() {
    if (this.overlay && this.overlay.parentNode) {
      this.overlay.style.opacity = "0";
      this.overlay.style.transition = "opacity 0.5s ease";
      setTimeout(() => {
        if (this.overlay && this.overlay.parentNode) {
          this.overlay.parentNode.removeChild(this.overlay);
          this.overlay = null;
        }
      }, 500);
    }
  }
  /**
   * 
   */
  setupFullscreenExitListener() {
    const handleFullscreenChange = () => {
      const isFullscreen = !!(document.fullscreenElement || document.webkitFullscreenElement || document.mozFullScreenElement || document.msFullscreenElement);
      if (!isFullscreen) {
        console.log("Exited fullscreen mode");
      }
    };
    document.addEventListener("fullscreenchange", handleFullscreenChange);
    document.addEventListener("webkitfullscreenchange", handleFullscreenChange);
    document.addEventListener("mozfullscreenchange", handleFullscreenChange);
    document.addEventListener("MSFullscreenChange", handleFullscreenChange);
  }
