    let minDist = Infinity;
    if (!player.isDead) {
      const d = this.mesh.position.distanceTo(player.mesh.position);
      target = player;
      minDist = d;
    }
    for (const dog of dogs) {
      if (dog.isDead) continue;
      const d = this.mesh.position.distanceTo(dog.mesh.position);
      if (d < minDist) {
        minDist = d;
        target = dog;
      }
    }
    if (!target) return;
    const dist = minDist;
