  initWorld() {
    const planeGeo = new PlaneGeometry(200, 200);
    const planeMat = new MeshStandardMaterial({
      color: 11193599,
      roughness: 0.2,
      metalness: 0.5,
      emissive: 4491451,
      emissiveIntensity: 0.2
    });
    const plane = new Mesh(planeGeo, planeMat);
    plane.rotation.x = -Math.PI / 2;
    plane.receiveShadow = true;
    this.scene.add(plane);
    const gridHelper = new GridHelper(200, 40, 16777215, 16777215);
    gridHelper.position.y = 0.02;
    gridHelper.material.transparent = true;
    gridHelper.material.opacity = 0.3;
    gridHelper.material.blending = AdditiveBlending;
    this.scene.add(gridHelper);
    const gridHelperSmall = new GridHelper(200, 100, 16777215, 8965375);
    gridHelperSmall.position.y = 0.01;
    gridHelperSmall.material.transparent = true;
    gridHelperSmall.material.opacity = 0.2;
    this.scene.add(gridHelperSmall);
    const neonColors = [16711935, 65535, 16776960, 65280, 16724787, 10027263];
    for (let i = 0; i < 200; i++) {
      const height = 2 + Math.random() * 12;
      const geo = new BoxGeometry(0.3, height, 0.3);
      const color = neonColors[Math.floor(Math.random() * neonColors.length)];
      const mat = new MeshBasicMaterial({ color });
      const mesh = new Mesh(geo, mat);
      mesh.position.set((Math.random() - 0.5) * 190, height / 2, (Math.random() - 0.5) * 190);
      this.scene.add(mesh);
    }
    for (let i = 0; i < 50; i++) {
      const geo = new TorusGeometry(1 + Math.random() * 2, 0.1, 8, 16);
      const color = neonColors[Math.floor(Math.random() * neonColors.length)];
      const mat = new MeshBasicMaterial({ color });
      const mesh = new Mesh(geo, mat);
      const x = (Math.random() - 0.5) * 180;
      const z = (Math.random() - 0.5) * 180;
      const y = 5 + Math.random() * 10;
      mesh.position.set(x, y, z);
      mesh.rotation.x = Math.PI / 2;
      this.scene.add(mesh);
    }
    const houseGroup = new Group();
    houseGroup.position.copy(this.safeHousePos);
    this.scene.add(houseGroup);
    const houseGeo = new BoxGeometry(6, 4, 6);
    this.houseMat = new MeshStandardMaterial({
      color: 2236962,
      roughness: 0.2,
      metalness: 0.8,
      transparent: true,
      opacity: 1
    });
    const house = new Mesh(houseGeo, this.houseMat);
    house.position.y = 2;
    houseGroup.add(house);
    const domeGeo = new SphereGeometry(3.5, 16, 16, 0, Math.PI * 2, 0, Math.PI / 2);
    this.domeMat = new MeshBasicMaterial({
      color: 65280,
      transparent: true,
      opacity: 0.3,
      side: DoubleSide
    });
    const dome = new Mesh(domeGeo, this.domeMat);
    dome.position.y = 4;
    houseGroup.add(dome);
    const signGeo = new PlaneGeometry(3, 1);
    const signMat = new MeshBasicMaterial({ color: 65280, side: DoubleSide });
    const sign2 = new Mesh(signGeo, signMat);
    sign2.position.set(0, 5, 3.1);
    houseGroup.add(sign2);
    const circleGeo = new RingGeometry(7, 7.5, 32);
    const circleMat = new MeshBasicMaterial({ color: 65280, side: DoubleSide });
    const circle = new Mesh(circleGeo, circleMat);
    circle.rotation.x = Math.PI / 2;
    circle.position.y = 0.1;
    houseGroup.add(circle);
  }
